package com.xk.dynamiczuuldemo.config;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.autoconfigure.web.ServerProperties;import org.springframework.boot.autoconfigure.web.servlet.WebMvcProperties;import org.springframework.cloud.netflix.zuul.filters.ZuulProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * Description: dynamic-zuul-demo * Created by xk on 2019/9/6 0006 10:47 */@Configurationpublic class NewZuulConfig {    @Autowired    private ZuulProperties zuulProperties;    @Autowired    private ServerProperties serverProperties;    @Bean    public NewZuulRouteLocator routeLocator() {        NewZuulRouteLocator routeLocator = new NewZuulRouteLocator(                this.serverProperties.getServlet().getContextPath(), this.zuulProperties);        return routeLocator;    }}